/* ====================================== */
/* 			Settings - Dropdown Checklist
/* ====================================== */

.ui-dropdownchecklist{
	z-index:1000;
}

.ui-dropdownchecklist-dropcontainer-wrapper{
	z-index:1000;
}

.ui-dropdownchecklist-selector {
	border: 1px solid #ddd;
	background: #fff url(../images/dropdown.png) no-repeat center right;
	height: 22px;
}
.ui-dropdownchecklist-selector:hover {
	background-image: url(../images/dropdown_hover.png);
	border-color: #5794bf;
}
.ui-dropdownchecklist-selector.ui-state-disabled {
	background-image: none !important;
	border-color: #ddd !important;
	color: #555;
}
.ui-dropdownchecklist-text {
	font-size: 12px;
	line-height: 15px;

	 padding: 3px 3px 3px ;
	margin-right: 17px; /* background dropdown.png image */
}

.ui-dropdownchecklist-dropcontainer {
	background-color: #fff;
	border: 1px solid #999;
}

.ui-dropdownchecklist-item {
	padding: 3px 3px 4px;
}

.ui-dropdownchecklist-item-hover {
	background-color: #39f;
}

.ui-dropdownchecklist-item-disabled label {
	color: #ccc;
}

.ui-dropdownchecklist-item-hover .ui-dropdownchecklist-text {
	color: #fff;
}

.ui-dropdownchecklist-group {
	font-weight: bold;
	font-style: italic;
	padding: 7px 5px 3px;
}

.ui-dropdownchecklist-indent {
	padding-left: 20px;
}


