	t.loadCssFile = function(urlCssFile,replaceID){

		var rand = Math.floor((Math.random()*100000)+1);

		urlCssFile += "?rand="+rand;

		if(replaceID)
			jQuery("#"+replaceID).remove();

		jQuery("head").append("<link>");
		var css = jQuery("head").children(":last");
		css.attr({
		      rel:  "stylesheet",
