		 */
		var updateSlidesOrder = function(sliderID){
			var arrSlideHtmlIDs = jQuery( "#list_slides" ).sortable("toArray");

			//get slide id's from html (li) id's
			var arrIDs = [];
			var orderCounter = 0;
