					throw CodeMirror.Pass; // Don't process the '>' at the end of an end-tag.
				}

				cm.replaceSelection('>'); // Mode state won't update until we finish the tag.
				pos = {line: pos.line, ch: pos.ch + 1};
				cm.setCursor(pos);

				tok = cm.getTokenAt(cm.getCursor());
