sharedir = environ.get("ROUTE_SHARE", path.join(path.dirname(__file__), 'test'))
localhost = environ.get("ROUTE_HOSTNAME", platform.node())
dnsport = int(environ.get("DNS_PORT", 53))
dnsaddr = environ.get("DNS_ADDR", '127.0.0.40')
